import styled from "styled-components";

export const Headline = styled.h1`
  font-size: 32px;
  color: inherit;
  letter-spacing: -0.75px;
  line-height: 1.15;
  font-weight: 700;
  margin: 0 0 20px;
`;

export const Caption = styled.p`
  color: var(--secondaryText);
  font-size: 19px;
  line-height: 1.5;
`;

export const Link = styled.a`
  color: var(--primaryColor);
`;
