import { Wrapper } from "../components/Layouts/";
import { H4 } from "../components/Typography/index";
import styled from "styled-components";
import { Button } from "../components/Button";

export default () => {
  return (
    <Wrapper>
      <HeroSection>
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            margin: "0 auto 10%",
            maxWidth: "576px",
          }}
        >
          <H4
            style={{
              color: "rgba(255,255,255,.95)",
              marginBottom: "48px",
            }}
          >
            I'm Georgemaine, a product designer working at Pitch where we're
            building the next-gen presentation tool. I love making tools so
            simple and fun to use that you'll want to use them all the time. I
            also enjoy making animation videos.
          </H4>
          <Button>Get in touch</Button>
        </div>
      </HeroSection>
    </Wrapper>
  );
};

export const HeroSection = styled.section`
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  background-color: #000;
  text-align: center;
  min-height: 660px;
  height: 93vh;
`;
