import { Wrapper } from "../components/Layouts/";
import { H4 } from "../components/Typography/index";
import styled from "styled-components";
import { Button } from "../components/Button";

export default () => {
  return (
    <Wrapper>
      <HeroSection>
        <div
          style={{
            textAlign: "center",
            marginBottom: "10%",
          }}
        >
          <H4
            style={{
              color: "#fff",
              marginBottom: "48px",
            }}
          >
            My name is Georgemaine Lourens and I’m a product designer at Pitch.
            I’m passionate about creating delightfull digital products and
            promotion video’s. Outside of work I love buying plants and
            traveling to different countries.
          </H4>
          <Button>Get in touch</Button>
        </div>
      </HeroSection>
    </Wrapper>
  );
};

export const HeroSection = styled.section`
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  background-color: #000;
  text-align: center;
  min-height: 660px;
  height: 93vh;
`;
