import { StickyNavList, StickyNav } from "./style";
import { Label } from "../Typography";
import { Button, RedButton } from "../Button";

// Declare Props
interface StickyNavProps {
  name: string;
  role: string;
  list: string[];
  button: string;
  onClick: Function;
  active: number;
}

// Create Function
export function StickyNavigation({
  name,
  role,
  list,
  button,
  onClick,
  active,
}: StickyNavProps) {
  const convertedActive = active >= 1 && active <= 4 ? 1 : active;

  React.useEffect(() => {
    console.log(convertedActive);
  });
  return (
    <StickyNav>
      <div>
        <Label color="#111" align="left">
          {name}
        </Label>
        <Label color="#bbb" align="left">
          {role}
        </Label>
      </div>
      <StickyNavList
        whileHover={{ backgroundColor: "#f7f7f7" }}
        whileTap={{ backgroundColor: "#e5e5e5" }}
      >
        {list.map((ListItem, index) => {
          return (
            <li
              key={index}
              onClick={() => onClick(index === 2 ? 5 : index === 3 ? 6 : index)}
            >
              <Button style={{ opacity: index != convertedActive && 0.6 }}>
                {ListItem}
              </Button>
            </li>
          );
        })}
      </StickyNavList>
      <RedButton>{button}</RedButton>
    </StickyNav>
  );
}
