import styled from "styled-components";

interface Props {
  animated: boolean;
}

export const Button = styled.button`
  -webkit-appearance: none;
  display: flex;
  align-items: center;
  font-size: 1.5rem;
  line-height: 1.2;
  font-weight: 400;
  justify-content: center;
  padding: 3px var(--spaceXXS);
  background: transparent;
  transition: all 0.2s;
  letter-spacing: 0.005em;
  border: none;
  margin: 0;
  outline: none;

  &:hover,
  &:focus {
    cursor: pointer;
  }
  &:active {
    font-weight: 600;
  }
`;

export const RedButton = styled(Button)`
  background: var(--red);
  line-height: 1.6;
  padding: var(--spaceXXS) var(--spaceS);
  border-radius: var(--spaceL);
`;

export const OpaqueButton = styled.button`
  position: relative;
  z-index: 2;
  flex: 1;
  background-color: transparent;
  white-space: nowrap;
  line-height: 1.6470588235;
  color: var(--primaryText);
`;

export const PopUpButtonChevron = styled.span`
  transform: ${(props: { animated: boolean }) =>
    props.animated ? "rotate(180deg)" : ""};
  margin: 0 0 0 var(--spaceXS);
  transition: transform 100ms ease;
  display: flex;
  align-items: center;
  justify-content: center;
`;
