import { Label } from "../components/Typography/index";
import styled from "styled-components";
import { useState, useEffect } from "react";
import { Button, RedButton } from "../components/Button";
import { motion } from "framer-motion";
import { StickyNavigation } from "../components/Navigation";

export default () => {
  const [activeMenuItem, setActiveMenuItem] = useState(0);
  function handleIndexChange(index: number) {
    setActiveMenuItem(index);
  }
  return (
    <Wrapper>
      <StickyNavigation
        name="Georgemaine Lourens"
        role="Product Designer"
        list={["About me", "Work", "Icons"]}
        button="Get in touch"
        active={activeMenuItem}
        onClick={handleIndexChange}
      />
      <Slides />
    </Wrapper>
  );
};

function Slides() {
  return (
    <motion.ul
      style={{
        margin: 0,
        padding: 0,
      }}
    >
      <li
        style={{
          cursor: "grab",
          display: "inline-block",
          position: "fixed",
          top: 168,
          right: 0,
          left: 0,
          bottom: 0,
          textAlign: "center",
          background: "var(--red)",
        }}
      >
        <h1>1</h1>
      </li>
    </motion.ul>
  );
}

const SticyNavStyle = {
  display: "flex",
  justifyContent: "space-between",
  position: "fixed" as "fixed",
  margin: "60px 60px 0",
  width: "calc(100% - 120px)",
};

interface StickyNavProps {
  name: string;
  role: string;
  list: string[];
  button: string;
  onClick: Function;
  active: number;
}
function StickyNav({
  name,
  role,
  list,
  button,
  onClick,
  active,
}: StickyNavProps) {
  return (
    <nav style={SticyNavStyle}>
      <div>
        <Label color="#111" align="left">
          {name}
        </Label>
        <Label color="#bbb" align="left">
          {role}
        </Label>
      </div>
      <motion.ul
        whileHover={{ backgroundColor: "#f7f7f7" }}
        whileTap={{ backgroundColor: "#e5e5e5" }}
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          padding: "var(--spaceXXS) var(--spaceXS)",
          margin: 0,
          borderRadius: "var(--spaceL)",
          backgroundColor: "#f2f2f2",
          transition: "backgroundColor .15s",
        }}
      >
        {list.map((ListItem, index) => {
          return (
            <li key={index} onClick={() => onClick(index)}>
              <Button style={{ opacity: index != active && 0.6 }}>
                {ListItem}
              </Button>
            </li>
          );
        })}
      </motion.ul>
      <RedButton>{button}</RedButton>
    </nav>
  );
}

export const Wrapper = styled.main`
  position: fixed;
  left: 0;
  right: 0;
  bottom: 0;
  top: 0;
  background-color: #fafafa;
  color: #111;
`;
