import { Wrapper } from "../components/Layouts/";
import { H4, H1, P } from "../components/Typography/index";
import styled from "styled-components";
import { Button } from "../components/Button";
import React from "react";
import { motion, useViewportScroll, useTransform } from "framer-motion";

export default () => {
  return (
    <Wrapper>
      <HeroSection>
        <Logo />
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            margin: "0 auto 10%",
            maxWidth: "698px",
          }}
        >
          <H4
            style={{
              color: "rgba(255,255,255,.95)",
              marginBottom: "48px",
            }}
          >
            I'm Georgemaine, a product designer at Pitch, helping build the
            next-gen presentation tool. I enjoy making tools so simple and fun
            to use that you'll want to use them all the time.
          </H4>
          <Button>Get in touch</Button>
        </div>
      </HeroSection>
      <section
        style={{
          width: "100%",
          position: "relative",
        }}
      >
        <Project
          src="/videos/mollie-mobile.mp4"
          headline="Mollie Mobile"
          caption="Designed apps for iOS, Android and developed the landing page • 2020"
        />
      </section>
    </Wrapper>
  );
};

export const HeroSection = styled.section`
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  background-color: #000;
  text-align: center;
  min-height: 660px;
  height: 93vh;
`;

function Project(props) {
  const { src, headline, caption, ...rest } = props;
  const { scrollYProgress } = useViewportScroll();
  const scale = useTransform(scrollYProgress, [0, 1], [1, 1.1]);
  const opacity = useTransform(scrollYProgress, [0.67, 1], [1, 0]);
  return (
    <div
      style={{
        position: "relative",
        zIndex: 46,
      }}
    >
      <motion.div
        style={{
          top: 0,
          left: 0,
          width: "100%",
          height: "100vh",
          position: "sticky",
          overflow: "hidden",
          scale,
          opacity,
        }}
      >
        <video
          autoPlay
          loop
          muted
          playsInline
          preload="auto"
          style={{
            width: "100%",
            height: "100%",
            maxWidth: "100%",
            verticalAlign: "middle",
            userSelect: "none",
            objectFit: "cover",
            zIndex: -1,
          }}
        >
          <source src={src} type="video/mp4" />
        </video>
      </motion.div>
      <div
        style={{
          position: "relative",
          zIndex: 10,
          marginTop: "-20vh",
          textAlign: "center",
          paddingBottom: "100vh",
        }}
      >
        <H1>{headline}</H1>
        <P>{caption}</P>
      </div>
    </div>
  );
}

function Logo() {
  return (
    <div
      style={{
        position: "relative",
      }}
    >
      <img
        src={"/images/gradient-background.png"}
        style={{
          maxWidth: 288,
          position: "absolute",
          top: -6,
          left: 0,
          right: 0,
          marginLeft: "auto",
          marginRight: "auto",
          mixBlendMode: "multiply",
          transform: "rotate(45deg)",
        }}
      />
      <svg
        width="360"
        height="360"
        viewBox="0 0 360 360"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path
          d="M300 60H180C113.725 60 60 113.725 60 180V187.5C60 249.63 110.37 300 172.5 300C234.63 300 285 249.63 285 187.5V120H180C146.865 120 120 146.865 120 180V187.5C120 216.495 143.505 240 172.5 240C201.495 240 225 216.495 225 187.5V180H180"
          stroke="white"
          strokeWidth="30"
          strokeMiterlimit="10"
        />
        <path
          d="M180 165.273C188.134 165.273 194.727 171.866 194.727 180C194.727 188.134 188.134 194.727 180 194.727C171.865 194.727 165.272 188.134 165.272 180C165.272 171.866 171.865 165.273 180 165.273Z"
          fill="white"
        />
      </svg>
    </div>
  );
}
