import { Label } from "../components/Typography/index";
import styled from "styled-components";
import React from "react";
import { Button } from "../components/Button";

export default () => {
  return (
    <Wrapper>
      <StickyNav
        name="Georgemaine Lourens"
        role="Product Designer"
        list={["About me", "Work", "Icons"]}
        button="Get in touch"
      />
    </Wrapper>
  );
};

const SticyNavStyle = {
  display: "flex",
  justifyContent: "space-between",
  position: "fixed" as "fixed",
  margin: "60px 60px 0",
  width: "calc(100% - 120px)",
};

function StickyNav(props: {
  name: string;
  role: string;
  list: string[];
  button: string;
}) {
  const { button, name, role, list } = props;
  return (
    <nav style={SticyNavStyle}>
      <div>
        <Label color="#111" align="left">
          {name}
        </Label>
        <Label color="#bbb" align="left">
          {role}
        </Label>
      </div>
      <ul
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          padding: "var(--spaceXXS) var(--spaceXS)",
          margin: 0,
          borderRadius: "var(--spaceM)",
          background: "#f2f2f2",
        }}
      >
        {list.map((ListItem, index) => {
          return (
            <li key={index}>
              <Button>{ListItem}</Button>
            </li>
          );
        })}
      </ul>
      <button>{button}</button>
    </nav>
  );
}

export const Wrapper = styled.main`
  position: fixed;
  left: 0;
  right: 0;
  bottom: 0;
  top: 0;
  background-color: #fafafa;
`;
