import styled from "styled-components";
import { useEffect, useRef, useState } from "react";
import { motion, useMotionValue } from "framer-motion";
import { StickyNavigation } from "../components/Navigation";

const menuListItems = ["Slide 0", "Slide 1", "Slide 2"];

export default () => {
  const [activeMenuItem, setActiveMenuItem] = useState(0);
  function handleIndexChange(index: number) {
    setActiveMenuItem(index);
  }

  return (
    <Wrapper>
      <StickyNavigation
        name="Georgemaine Lourens"
        role="Product Designer"
        list={menuListItems}
        button="Get in touch"
        active={activeMenuItem}
        onClick={handleIndexChange}
      />
      <Slides active={activeMenuItem} list={menuListItems} />
    </Wrapper>
  );
};

interface SlidesProps {
  list: string[];
  active: number;
}

function Slides({ list, active }: SlidesProps) {
  const calculateXPosition = (active, index) => {
    // Make the menu item that I click visisble
    // Move the menu items previous to the one I clicked to the left
    // Move the menu items upcoming to the one I clicked to the right
    // If the item that I click one is already visisble, then do nothing

    if (active === index) {
      `calc(0 * 100vw)`;
    } else {
      `calc(${index} * 100vw)`;
    }
  };

  return (
    <motion.ul
      style={{
        margin: 0,
        padding: 0,
      }}
    >
      {list.map((listItem, index) => {
        useEffect(() => {
          console.log(index);
        });
        return (
          <motion.li
            animate={{
              x:
                active === index
                  ? `0vw`
                  : active < index
                  ? `calc(${index} - 100vw)`
                  : `calc(${index} * 100vw)`,
            }}
            key={index}
            transition={{
              type: "spring",
              damping: 450,
              stiffness: 10,
            }}
            style={{
              cursor: "grab",
              display: "inline-block",
              position: "fixed",
              top: 168,
              right: 0,
              left: 0,
              bottom: 0,
              textAlign: "center",
              background: "var(--red)",
            }}
          >
            <h1>{listItem}</h1>
          </motion.li>
        );
      })}
    </motion.ul>
  );
}

export const Wrapper = styled.main`
  position: fixed;
  left: 0;
  right: 0;
  bottom: 0;
  top: 0;
  background-color: #fafafa;
  color: #111;
`;
