import styled from "styled-components";
import { useState } from "react";
import { motion } from "framer-motion";
import { StickyNavigation } from "../components/Navigation";
import { listenerCount } from "process";
const menuListItems = ["About me", "Work", "Icons"];

export default () => {
  const [activeMenuItem, setActiveMenuItem] = useState(0);
  function handleIndexChange(index: number) {
    setActiveMenuItem(index);
  }
  return (
    <Wrapper>
      <StickyNavigation
        name="Georgemaine Lourens"
        role="Product Designer"
        list={menuListItems}
        button="Get in touch"
        active={activeMenuItem}
        onClick={handleIndexChange}
      />
      <Slides active={activeMenuItem} list={menuListItems} />
    </Wrapper>
  );
};

interface SlidesProps {
  list: string[];
  active: number;
}

function Slides({ list, active }: SlidesProps) {
  return (
    <motion.ul
      style={{
        margin: 0,
        padding: 0,
      }}
    >
      {list.map((listItem, index) => {
        return (
          <motion.li
            initial={{ scale: 1 }}
            animate={{
              opacity: index === active ? 1 : 0,
              scale: index === active ? 1 : index < active ? 0.45 : 0.45,
              x:
                index === active
                  ? 0
                  : index < active
                  ? "-100vw"
                  : `calc(${index} * 100vw)`,
            }}
            key={index}
            transition={{
              type: "spring",
              damping: 450,
              stiffness: 57,
            }}
            style={{
              cursor: "grab",
              display: "inline-block",
              position: "fixed",
              top: 168,
              right: 0,
              left: 0,
              bottom: 0,
              textAlign: "center",
              background: "var(--red)",
            }}
          >
            <h1>{listItem}</h1>
          </motion.li>
        );
      })}
    </motion.ul>
  );
}

export const Wrapper = styled.main`
  position: fixed;
  left: 0;
  right: 0;
  bottom: 0;
  top: 0;
  background-color: #fafafa;
  color: #111;
`;
