import { Wrapper } from "../components/Layouts/";
import {
  InlineA,
  H1,
  P,
  Manifesto,
  Caption,
  Label,
} from "../components/Typography/index";
import styled from "styled-components";
import { Button } from "../components/Button";
import React from "react";
import { motion, useViewportScroll, useTransform } from "framer-motion";

const MobileAppLabels = [
  {
    headline: "Expertise",
    caption: "Product design",
  },
  {
    headline: "Platforms",
    caption: "iOS, Android",
  },
  {
    headline: "Timeline",
    caption: "2019 - 2020",
  },
];
const MollieVideoLabels = [
  {
    headline: "Expertise",
    caption: "Design, Animation, Video editing",
  },
  {
    headline: "Platforms",
    caption: "Video",
  },
  {
    headline: "Timeline",
    caption: "Q3 2019",
  },
];
const MollieCheckoutLabels = [
  {
    headline: "Expertise",
    caption: "Product Design, Development",
  },
  {
    headline: "Platforms",
    caption: "Web",
  },
  {
    headline: "Timeline",
    caption: "Q2 2019",
  },
];

export default () => {
  return (
    <Wrapper>
      <HeroSection>
        <Logo />
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            margin: "0 auto 10%",
            maxWidth: "828px",
          }}
        >
          <Manifesto
            style={{
              color: "rgba(255,255,255,.95)",
              marginBottom: "48px",
            }}
          >
            I'm Georgemaine, a product designer at{" "}
            <InlineA
              href="https://pitch.com"
              target="_blank"
              rel="nofollow noopener noreferrer"
              color="#7A7AFF"
            >
              Pitch
            </InlineA>
            , helping to build the next-gen presentation tool. I enjoy making
            tools so simple and fun to use that you'll want to use them all the
            time. I also like making animation videos from time to time
          </Manifesto>
          <Button>Get in touch</Button>
        </div>
      </HeroSection>
      <section
        style={{
          width: "100%",
          position: "relative",
        }}
      >
        <Project
          src="/videos/mollie-mobile.mp4"
          yOpacityRange={[0.19, 0.36]}
          yScaleRange={[0, 0.36]}
          opacityRange={[0.8, 0]}
          scaleRange={[0.95, 1.25]}
          headline="Mollie Mobile Apps"
          caption="Designed apps to make your phone a place where you can quickly manage payments and watch your business grow."
          labels={MobileAppLabels}
        />
        <Project
          copyMarginTop={"0"}
          zIndex={5}
          src="/videos/mollie-video.mp4"
          yOpacityRange={[0.36, 0.38, 0.44, 0.57]}
          yScaleRange={[0.38, 0.57]}
          opacityRange={[0, 0.8, 0.8, 0]}
          scaleRange={[1, 1.25]}
          marginTop="-100vh"
          headline="Mollie Video"
          labels={MollieVideoLabels}
          caption="To help Marketing showcase Mollie at events, I created an engaging video that shows how Mollie works in a website."
        />
        <Project
          copyMarginTop={"0"}
          zIndex={5}
          src="/videos/mollie-checkout.mp4"
          yOpacityRange={[0.57, 0.59, 0.65, 0.78]}
          yScaleRange={[0.59, 0.78]}
          opacityRange={[0, 0.8, 0.8, 0]}
          scaleRange={[1, 1.25]}
          marginTop="-100vh"
          headline="Mollie Checkout"
          labels={MollieCheckoutLabels}
          caption="Redesigned and developed the Mollie Checkout so that people can efficiently pay for products with confidence."
        />
        <Project
          copyMarginTop={"0"}
          zIndex={5}
          src="/videos/mollie-apple-pay.mp4"
          yOpacityRange={[0.78, 0.8, 0.86, 1]}
          yScaleRange={[0.8, 1]}
          opacityRange={[0, 0.8, 0.8, 0]}
          scaleRange={[1, 1.25]}
          marginTop="-100vh"
          headline="Mollie & Apple Pay"
          labels={MobileAppLabels}
          caption="To help launch Apple Pay at Mollie, I created an engaging promotional video that inspired merchants to integrate Apple Pay."
        />
      </section>
    </Wrapper>
  );
};

export const HeroSection = styled.section`
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  background-color: #000;
  text-align: center;
  min-height: 660px;
  height: 93vh;
`;

function Project(props) {
  const {
    yOpacityRange,
    opacityRange,
    yScaleRange,
    scaleRange,
    labels,
    src,
    headline,
    caption,
    marginTop,
    zIndex,
    copyMarginTop,
  } = props;
  const { scrollYProgress } = useViewportScroll();

  const scale = useTransform(scrollYProgress, yScaleRange, scaleRange);
  const opacity = useTransform(scrollYProgress, yOpacityRange, opacityRange);

  return (
    <div
      style={{
        position: "relative",
        zIndex: zIndex ? zIndex : 6,
        marginTop: marginTop || "",
      }}
    >
      <motion.div
        style={{
          top: 0,
          left: 0,
          width: "100%",
          height: "100vh",
          position: "sticky",
          overflow: "hidden",

          opacity,
        }}
      >
        <motion.video
          autoPlay
          loop
          muted
          playsInline
          preload="auto"
          style={{
            width: "100%",
            height: "100%",
            position: "absolute",
            top: 0,
            left: 0,
            verticalAlign: "middle",
            userSelect: "none",
            objectFit: "cover",
            zIndex: -1,
            scaleY: scale,
            scaleX: scale,
          }}
        >
          <source src={src} type="video/mp4" />
        </motion.video>
      </motion.div>
      <div
        style={{
          position: "relative",
          zIndex: 10,
          marginTop: copyMarginTop ? copyMarginTop : "-20vh",
          textAlign: "center",
          paddingBottom: "100vh",
          width: "100%",
          marginLeft: "auto",
          marginRight: "auto",
          maxWidth: 686,
        }}
      >
        <H1>{headline}</H1>
        <Caption>{caption}</Caption>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
          }}
        >
          {labels.map((label, index) => {
            return (
              <div
                key={index}
                style={{
                  width: "100%",
                }}
              >
                <Label>
                  <strong>{label.headline}</strong>
                </Label>
                <Label>{label.caption}</Label>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

function Logo() {
  const css = `
  @-webkit-keyframes circular {
    100% {
      transform: rotate(90deg);
    }
}`;
  return (
    <div
      style={{
        position: "relative",
      }}
    >
      <img
        src={"/images/gradient-background.png"}
        style={{
          maxWidth: 312,
          position: "absolute",
          top: -6,
          left: 0,
          right: 0,
          marginLeft: "auto",
          marginRight: "auto",
          mixBlendMode: "multiply",
          animationName: "circular",
          animationDuration: "12s",
          animationIterationCount: "infinite",
          animationDirection: "alternate",
        }}
      />
      <style>{css}</style>
      <svg
        width="360"
        height="360"
        viewBox="0 0 360 360"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path
          d="M300 60H180C113.725 60 60 113.725 60 180V187.5C60 249.63 110.37 300 172.5 300C234.63 300 285 249.63 285 187.5V120H180C146.865 120 120 146.865 120 180V187.5C120 216.495 143.505 240 172.5 240C201.495 240 225 216.495 225 187.5V180H180"
          stroke="white"
          strokeWidth="30"
          strokeMiterlimit="10"
        />
        <path
          d="M180 165.273C188.134 165.273 194.727 171.866 194.727 180C194.727 188.134 188.134 194.727 180 194.727C171.865 194.727 165.272 188.134 165.272 180C165.272 171.866 171.865 165.273 180 165.273Z"
          fill="white"
        />
      </svg>
    </div>
  );
}
