import styled from "styled-components";

export const Headline = styled.h1`
  font-size: 32px;
  color: inherit;
  letter-spacing: -0.75px;
  line-height: 1.15;
  font-weight: 700;
  margin: 0 0 20px;
`;

export const Caption = styled.p`
  width: 100%;
  height: 150px;
  font-family: "Sohne-Kraftig", "Söhne", serif;
  color: #ccc;
  font-size: 22px;
  letter-spacing: 0px;
  line-height: 1.3;
  font-weight: 500;
  text-align: left;
`;
