import { createGlobalStyle } from "styled-components";

export default createGlobalStyle`
  :root {
    --background: #0F0F0F;
    --primaryText: #FFF;
    --secondaryText: #CCC;
    --primaryColor: #FFC1A9;
    --primaryColorHover: #FCA;
    --dribbbleColor: #FAC;
    --dribbbleColorHover: #FCE;
    --twitterColor: #6CF;
    --twitterColorHover: #0CF;
    }
`;
